import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import React, { useRef } from 'react';
import './Product.css'
import Button from "react-bootstrap/esm/Button";

export function Product(props) {
    const [Product, setProduct] = useState([]);
    const { catmasterid } = useParams();
    useEffect(() => {
        fetch("http://localhost:8080/api/productsByCat/" + catmasterid)
            .then(res => res.json())
            .then((result) => { setProduct(result); }
            );
    }, []);

    const [Cart, setCart] = useState({});
    // const handleChange = (event) => {
    //     const name = event.target.name;
    //     const value = event.target.value;
    //     setCart(values => ({ ...values, [name]: value }))
    // }
    setCart({"prod_Id": 1,  })
    const addtocart = (event) => {
        let demo = JSON.stringify(Cart);
        fetch("http://localhost:8080/api/addCart", {
            method: 'POST',
            headers: { 'Content-type': 'application/json' },
            body: demo
        })
        event.preventDefault();
    }


    return (
        <div>
            <div className='catcontainer'>

                {Product.map(pro => (

                    // <Link to={'/SubCategories/' + pro.prod_Id}>
                        <div className='citem'>
                            <img className='cimg' src={pro.prodImage}></img><br />
                            <label><b>{pro.prodName}</b></label><br />
                            <label><b>{pro.prodDescription}</b></label><br />
                            <label><b>MRP:{pro.mrpPrice}</b></label><br />
                            <label><b>CardHolders Price:{pro.cardholdersPrice}</b></label><br />
                            <label><b>Points To Be Redeem:{pro.pointsToBeRedeem}</b></label><br />
                            <Button onClick={addtocart}>Add to cart</Button>
                        </div>
                    // </Link>

                ))}

                {/* {Product.map(pro => (       
                    <form >
                        <input type="text" name="prod_Id" value={pro.prod_Id} onChange={handleChange}/><br />
                        <input type="text" name="deliveryCharges" value='100' onChange={handleChange}/><br />
                        <input type="text" name="discount" value='50' onChange={handleChange}/><br />
                        <input type="text" name="price" value={pro.mrpPrice} onChange={handleChange}/><br />
                        <input type="text" name="quantity" value='1' onChange={handleChange}/><br />
                        <input type="text" name="total" value={pro.mrpPrice+50} onChange={handleChange}/><br />
                        <input type="text" name="cust_Id" value='null' onChange={handleChange}/><br />
                        <input type="submit" value="Add to cart" /><br />
                    </form>
                ))} */}

            </div>
        </div>
    );
}
