import React, { useState } from "react";
import "./AuthForm.css"; // Import your CSS file for styling

export function AuthForm() {
  const [isLogin, setIsLogin] = useState(true);

  const handleSlideChange = () => {
    setIsLogin(!isLogin);
  };

  return (
    <div className="wrapper">
      <div className="title-text">
        <div className={`title ${isLogin ? "login" : "signup"}`}>
          {isLogin ? "Login Form" : "Signup Form"}
        </div>
      </div>
      <div className="form-container">
        <div className="slide-controls">
          <input type="radio" name="slide" id="login" checked={isLogin} />
          <input type="radio" name="slide" id="signup" checked={!isLogin} />
          <label htmlFor="login" className={`slide login ${isLogin ? "active" : ""}`} onClick={handleSlideChange}>
            Login
          </label>
          <label htmlFor="signup" className={`slide signup ${!isLogin ? "active" : ""}`} onClick={handleSlideChange}>
            Signup
          </label>
          <div className="slider-tab"></div>
        </div>
        <div className="form-inner">
          {/* Your login and signup forms */}
          {isLogin ? (
            <form className="login">
              {/* Login form fields */}
            </form>
          ) : (
            <form className="signup">
              {/* Signup form fields */}
            </form>
          )}
        </div>
      </div>
    </div>
  );
}
