import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export function CategoryBySubcatId(props) {
    const [Category, setCategory] = useState([]);
    const { id } = useParams()
    useEffect(() => {
        fetch("http://localhost:8080/api/categorybysubcatid/"+id)
            .then(res => res.json())
            .then((result) => { setCategory(result); }
            );
    }, []);

    return (
        <>
            <div className='catcontainer'>

                {Category.map(cat => (

                    <a>
                        <div className='citem'>
                            <img className='cimg' src={cat.catImgPath}></img><br />
                            <label><b>{cat.categoryName}</b></label><br />
                        </div>
                    </a>

                ))}

            </div>
        </>
    );
}
