import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import './CategoryBySubcatId.css'

export function CategoryBySubcatId(props) {
    const [Category, setCategory] = useState([]);
    const { subcatid } = useParams();
    useEffect(() => {
        fetch("http://localhost:8080/api/category/" + subcatid)
            .then(res => res.json())
            .then((result) => { setCategory(result); }
            );
    }, []);

    return (
        <>
            <div className='catcontainer'>
                {Category.map(cat => (

                    <Link to={'/CategoryBySubcatId/'+cat.cat_id}>
                        <div className='citem'>
                            <img className='cimg' src={'/'+cat.catImgPath} alt={cat.catShortDesc}></img><br />
                            <label><b>{cat.categoryName}</b></label><br />
                        </div>
                       
                    </Link>

                ))}


            </div>
        </>
    );
}
