import { useState, useEffect } from "react";
export function AddCart() {

    const [Cart, setCart] = useState({});
    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setCart(values => ({ ...values, [name]: value }))
    }
    const addtocart = (event) => {
        let demo = JSON.stringify(Cart);
        fetch("http://localhost:8080/api/addCart", {
            method: 'POST',
            headers: { 'Content-type': 'application/json' },
            body: demo
        })
        event.preventDefault();
    }

    return (
        <div>
            <div className='catcontainer'>
                <form >
                    <input type="text" name="prod_Id" onChange={handleChange} /><br />
                    <input type="text" name="deliveryCharges" onChange={handleChange} /><br />
                    <input type="text" name="discount" onChange={handleChange} /><br />
                    <input type="text" name="price" onChange={handleChange} /><br />
                    <input type="text" name="quantity" onChange={handleChange} /><br />
                    <input type="text" name="total" onChange={handleChange} /><br />
                    <input type="text" name="cust_Id" onChange={handleChange} /><br />
                    <input type="submit" value="Add to cart" /><br />
                </form>
            </div>
        </div>
    );
}
