import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";

export function Login() {
  const [user, setUser] = useState({
    Username: "",
    Password: "",
  });

  const handleChange = (e) => {
    const value = e.target.value;
    setUser({ ...user, [e.target.name]: value });
  };

  const handleClick = async () => {
    console.log(user);
  
    try {
      
      const response = await fetch(`http://localhost:8080/api/getbyusername/`+user.Username);
      
      if (response.ok) {
        const responseData = await response.json();
        console.log(responseData);

        if (responseData.uname===user.Username&&responseData.pass===user.Password) {
          alert("Welcome " + user.Username);
          //dispatch(login({ userName: user.Username }));
        } else {
          alert("Enter Valid Credentials");
        }
      } else {
        alert("Error fetching data");
      }
    } catch (error) {
      alert("Enter Valid Credential")
      console.error("An error occurred:", error);
    }
  };
  

  return (
    <div className='login-container'>
      <Form>
        <Form.Group>
          <Form.Label>Enter Username</Form.Label>
          <Form.Control type='text' name='Username' />
        </Form.Group>
        <Form.Group>
          <Form.Label>Enter Password</Form.Label>
          <Form.Control type='password' name='Password' autoComplete='false' />
        </Form.Group>
        <Button className='login-button'>Login</Button>
        <div>
          <a href='/Register'> Not a Customer?</a>
          <Button className='Register-button' href='/Register'>
            Logout
          </Button>
        </div>
      </Form>
    </div>
  );
}

export default Login;
