import React, { useEffect, useState } from "react";
import { Button, Card, Col, ListGroup, Row } from "react-bootstrap";
import { Link, useParams } from "react-router-dom";
import "../assests/Products.css"; // Import your custom CSS file for styling

export function Products() {
  const [products, setProducts] = useState([]);
  const [cartdto, setCartdto] = useState([]);
  const [xxx, setXxx] = useState({});

  const { cat_Id } = useParams();

  const mrpPriceStyle = {
    textDecoration: "line-through", // Add the strikethrough effect
  };

  useEffect(() => {
    fetch("http://localhost:8080/api/getProductsByBrands/" + cat_Id)
      .then((res) => res.json())
      .then((result) => setProducts(result));
  }, [cat_Id]);

  const handleClick = (event, prod) => {
    console.log("Product added to cart:", prod.prod_Id);

    let p = prod.prod_Id;
    console.log("hello");
    let x = {
      cart_Id: 1,
      prod_Id: p,
      cust_Id: 1,
    };

    let demo = JSON.stringify(x);
    fetch("http://localhost:8080/api/addAll", {
      method: "POST",
      headers: { "Content-type": "application/json" },
      body: demo,
    }).then((res) => {
      console.log("ok");
    });
  };

  return (
    <>
      <Row>
        {products.map((prod) => (
          <Col>
            <Card border='info' style={{ width: "18rem" }}>
              <Card.Header>{prod.prodName}</Card.Header>
              <Card.Body>
                <Card.Img src={prod.prodImage}></Card.Img>
                <label>
                  MRP Price:
                  <Card.Title style={mrpPriceStyle}>{prod.mrpPrice}</Card.Title>
                </label>
                <Card.Title>Price: {prod.cardholdersPrice}</Card.Title>
                <Card.Text>{prod.prodDescription}</Card.Text>
              </Card.Body>
              <Button onClick={(event) => handleClick(event, prod)}>
                Add to Cart
              </Button>
            </Card>
          </Col>
        ))}
      </Row>
    </>
  );
}
