import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, Button } from "react-bootstrap";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";

export default function Cart() {
  const [cartItems, setCartItems] = useState([]);
  const [products, setProducts] = useState([]);
  const [total, setTotal] = useState(0);

  const cust_Id = useSelector((state) => {
    return state.user.cust_Id;
  });

  useEffect(() => {
    fetch("http://localhost:8080/api/getCartByCustomer/" + cust_Id)
      .then((res) => res.json())
      .then((result) => {
        setProducts(result);

        let totalPrice = 0;
        result.forEach((item) => {
          totalPrice += item.product.cardholdersPrice;
        });
        setTotal(totalPrice);
        console.log(products);
      });
  }, []);

  let delivercharge = total < 1000 ? 100 : 0;

  const navigate = useNavigate();
  const OpenInvoice = () => {
    let x = {
      invDate: "2023-08-31",
      totalAmt: total,
      tax: 15.0,
      payableAmt: total - delivercharge,
      cust_Id: cust_Id,
    };

    let demo = JSON.stringify(x);

    fetch("http://localhost:8080/api/addInvoice", {
      method: "POST",
      headers: { "Content-type": "application/json" },
      body: demo,
    });

    let y = {
      quantity: 2,
      delivery_Charges: delivercharge,
      discount: 20.0,
      price: total - delivercharge - 20,
      total: total,
      cust_Id: cust_Id,
    };
    let demo1 = JSON.stringify(y);
    fetch("http://localhost:8080/api/addCart", {
      method: "POST",
      headers: { "Content-type": "application/json" },
      body: demo1,
    });

    setTimeout(() => {
      navigate("/Invoice/");
    }, 2000);
  };

  // ...
  const handleRemove = (itemId) => {
    fetch("http://localhost:8080/api/deleteCart/" + itemId, {
      method: "DELETE",
    })
      .then((res) => {
        if (res.ok) {
          setProducts((prevProducts) =>
            prevProducts.filter((item) => item.cart_Id !== itemId)
          );
        } else {
          console.error("Failed to delete item from the database.");
        }
      })
      .catch((error) => {
        console.error("Error deleting item:", error);
      });
      setTotal(to);
    navigate("/Cart");
  };

  const pointsToManage = (event, item) => {
    if (event.target.checked) {
      setTotal(total - item.product.pointsToBeRedeem);
    } else {
      setTotal(total + item.product.pointsToBeRedeem);
    }
  };

  return (
    <Container>
      <h1>Your Cart</h1>
      {products.map((item) => (
        <Card key={item.cart_Id} className='mb-3'>
          <Card.Body>
            <Row>
              <Col md={4}>
                <img
                  src={item.product.prodImage}
                  alt={item.product.prodName}
                  className='img-fluid'
                  style={{ maxWidth: "100px" }}
                />
              </Col>
              <Col md={8}>
                <h5>{item.product.prodName}</h5>
                <p>Price: ${item.product.cardholdersPrice}</p>
                <div>
                  <Button
                    variant='secondary'
                    size='sm'
                    onClick={() => {
                      // Implement increment logic here
                    }}
                  >
                    +
                  </Button>
                  <input
                    type=''
                    value={1}
                    onChange={(event) => {}}
                    style={{ width: "40px" }}
                  />
                  <Button
                    variant='secondary'
                    size='sm'
                    onClick={() => {
                      // Implement decrement logic here
                    }}
                  >
                    -
                  </Button>
                </div>
                <input
                  type='checkbox'
                  id='scales'
                  name='scales'
                  onChange={(event) => pointsToManage(event, item)}
                />
                <label for='scales'>
                  cardholdersPrice: {item.product.cardholdersPrice} -{" "}
                  {item.product.pointsToBeRedeem}
                </label>
                <br></br>
                <Button
                  variant='danger'
                  size='sm'
                  onClick={() => handleRemove(item.cart_Id)}
                >
                  Remove
                </Button>
              </Col>
            </Row>
          </Card.Body>
        </Card>
      ))}
      <div className='text-right'>
        <h4>DeliveryCarges:$ {delivercharge} </h4>
        <h4>Total:$ {total} </h4>

        <Button variant='outline-info' onClick={OpenInvoice}>
          Checkout
        </Button>
      </div>
    </Container>
  );
}
