import { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import { useNavigate } from "react-router-dom"

export function Cart(props) {
    let navigate = useNavigate();
    const [Cart, setCart] = useState([]);
    const Uobj = useSelector((state) => {
        return state.user;
    });
    useEffect(() => {
        fetch("http://localhost:8080/api/getCartByCustomer/" + Uobj.cust_Id)
            .then(res => res.json())
            .then((result) => { setCart(result); }
            );
    }, []);

    const handleDelete = (event, cart) => {
        fetch("http://localhost:8080/api/deleteCart/" + cart.cart_Id, {
            method: 'Delete'
        })
        navigate('/Cart');
    }

    let price;
    const handleredeem = (event, cart) => {
        if(event.rp.checked){
            price = cart.car
        }
    }

    return (
        <div>
            <div className='catcontainer'>

                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th></th>
                                <th>Product Name</th>
                                <th>Price</th>
                                <th>Redeem Points</th>
                            </tr>
                        </thead>
                        <tbody>
                            {Cart.map(cart => (
                                <tr key={cart.cart_Id}>
                                    <td><img src={cart.product.prodImage}></img></td>
                                    <td>{cart.product.prodName}</td>
                                    <td>{cart.product.mrpPrice}</td>
                                    <td>
                                        <input name="rp" type="checkbox" onClick={(event) => handleredeem(event, cart)}></input>
                                        {cart.product.pointsToBeRedeem}
                                    </td>
                                    {/* <td><Button variant="primary"><a style={mystyle1} href={'/Product/' + pro.proid}>View</a></Button></td>
                                    <td><Button variant="secondary"><a style={mystyle1} href={'/Proedit/' + pro.proid}>Edit</a></Button></td> */}
                                    <td><Button variant="danger" onClick={(event) => handleDelete(event, cart)}>Remove</Button></td>
                                </tr>
                                
                            ))}
                            <tr>
                                <td>Total</td>
                                <td></td>
                                <td></td>
                                <td></td>
                                <td></td>
                            </tr>
                        </tbody>
                    </Table>

            </div>
        </div>
    );
}
