import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import React, { useRef } from 'react';
import './Product.css'
import Button from "react-bootstrap/esm/Button";
import Card from "react-bootstrap/Card";

export function Product(props) {
    const [Product, setProduct] = useState([]);
    const { catmasterid } = useParams();
    useEffect(() => {
        fetch("http://localhost:8080/api/productsByCat/" + catmasterid)
            .then(res => res.json())
            .then((result) => { setProduct(result); }
            );
    }, []);

    
    const addtocart = (event, pro) => {
        console.log(pro);
        const cart = { "product": {
            "prod_Id": 1,
            "prodName": "HP ChromeBook 11",
            "prodDescription": "HP ChromeBook is a Chrome OS laptop with a 11.6-inch display that has a resolution of 1366x768 pixels.",
            "mrpPrice": 25000.0,
            "prodImage": "/Images/Electronics/Laptop/HP Chromebook 11.jpg",
            "cardholdersPrice": 23500.0,
            "pointsToBeRedeem": 1500
        }, "customer": {
            "cust_Id": 1,
            "custName": "pady",
            "phoneNo": 7718979317,
            "email": "pady@gmail.com",
            "gender": "M",
            "redeemPoints": 10000,
            "addLine1": "thane",
            "addLine2": "thane",
            "city": "thane",
            "pincode": 400607,
            "invoices": []
        } };
        let demo = JSON.stringify(cart);
        fetch("http://localhost:8080/api/addCart", {
            method: 'POST',
            headers: { 'Content-type': 'application/json' },
            body: demo
        })
        event.preventDefault();
        alert("Product added to cart");
    }


    return (
        <div>
            <div className='catcontainer'>

                {Product.map(pro => (

                    <Card className="procard">
                        <Card.Body>
                        <Card.Img className="img" src={pro.prodImage}></Card.Img><br/><br/>
                        <Card.Title className="title">{pro.prodName}</Card.Title>
                        <Card.Text>{pro.prodDescription}</Card.Text>
                        <Card.Text>
                            <b>MRP:{pro.mrpPrice}</b><br/>
                            CardHolders Price:{pro.cardholdersPrice}<br/>
                            Points To Be Redeem:{pro.pointsToBeRedeem}
                        </Card.Text>
                        <Button onClick={(event)=>addtocart(event, pro)}>Add to cart</Button>
                        </Card.Body>
                    </Card>

                ))}

            </div>
        </div>
    );
}
