import { useState, useEffect } from "react";
import './Categories.css'
import { Link } from "react-router-dom";

export function Categories(props) {
    const [Category, setCategories] = useState([]);
    useEffect(() => {
        fetch("http://localhost:8080/api/category/"+'null')
            .then(res => res.json())
            .then((result) => { setCategories(result); }
            );
    }, []);

    return (
        <div>
            <div className='catcontainer'>
                {Category.map(cat => (
                    
                    <Link to={'/SubCategories/'+cat.cat_Id}>
                        <div className='citem'>
                            <img className='cimg' src={cat.catImgPath}></img><br />
                            <label><b>{cat.categoryName}</b></label><br />
                        </div>
                    </Link>

                ))}
                <Row>
      {Category.map((cat) => (
        <Col>
          <Link to={"/SubCategory/" + cat.cat_Id}>
            <Card border='info' style={{ width: "18rem" }}>
              <Card.Header>{cat.categoryName}</Card.Header>
              <Card.Body>
                <Card.Img src={cat.catImgPath}></Card.Img>
                <Card.Title></Card.Title>
                <Card.Text></Card.Text>
              </Card.Body>
            </Card>
          </Link>
        </Col>
      ))}
    </Row>

            </div>
        </div>
    );
}
