import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import React, { useRef } from 'react';
import './Product.css'
import Button from "react-bootstrap/esm/Button";


export function Product(props) {
    const [Product, setProduct] = useState([]);
    const { catmasterid } = useParams();
    useEffect(() => {
        fetch("http://localhost:8080/api/productsByCat/" + catmasterid)
            .then(res => res.json())
            .then((result) => { setProduct(result); }
            );
    }, []);

    const [Cart, setCart] = useState({});
    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setCart(values => ({ ...values, [name]: value }))
    }
    const addtocart = (event) => {
        let demo = JSON.stringify(Cart);
        fetch("http://localhost:8080/api/addCart", {
            method: 'POST',
            headers: { 'Content-type': 'application/json' },
            body: demo
        })
        event.preventDefault();
    }

    const formRef = useRef(null);
    const handleSubmitButtonClick = () => {
        // Call the submit method on the form element
        if (formRef.current) {
          formRef.current.submit();
        }
      };

    return (
        <div>
            <div className='catcontainer'>

                {Product.map(pro => (

                    <Link to={'/SubCategories/' + pro.prod_Id}>
                        <div className='citem'>
                            <img className='cimg' src={pro.prodImage}></img><br />
                            <label><b>{pro.prodName}</b></label><br />
                            <label><b>{pro.prodDescription}</b></label><br />
                            <label><b>MRP:{pro.mrpPrice}</b></label><br />
                            <label><b>CardHolders Price:{pro.cardholdersPrice}</b></label><br />
                            <label><b>Points To Be Redeem:{pro.pointsToBeRedeem}</b></label><br />
                            <Button onClick={handleSubmitButtonClick}>Add to cart</Button>
                        </div>
                    </Link>

                ))}
                
                {Product.map(pro => (
                    <Form style={{ border: "1px solid lightgrey", padding: "20px", background: "lightgrey" }} onSubmit={handleSubmit}>
                    <h2>Create Product:</h2>
                    <Row>
                        <Form.Group as={Col} md="4" controlId="validationCustom01">
                            <Form.Label>Id</Form.Label>
                            <Form.Control
                                type="text"
                                name="proid"
                                onChange={handleChange}
                            />
                            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                        </Form.Group>
                        <Form.Group as={Col} md="4" controlId="validationCustom02">
                            <Form.Label>Name</Form.Label>
                            <Form.Control
                                required
                                type="text"
                                name="proname"
                                onChange={handleChange}
                            />
                            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                        </Form.Group>
                    </Row>
                    <br />
                    <Row>
                        <Form.Group as={Col} md="4" controlId="validationCustomUsername">
                            <Form.Label>Category</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="category"
                                    onChange={handleChange}
                                    required
                                />
                                <Form.Control.Feedback type="invalid">
                                    Please choose a username.
                                </Form.Control.Feedback>
                        </Form.Group>
                        <Form.Group as={Col} md="4" controlId="validationCustom01">
                            <Form.Label>Quantity</Form.Label>
                            <Form.Control
                                required
                                type="text"
                                name="quantity"
                                onChange={handleChange}
                            />
                            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                        </Form.Group>
                        </Row>
                        <br/>
                        <Row>
                        <Form.Group as={Col} md="4" controlId="validationCustom01">
                            <Form.Label>Price</Form.Label>
                            <Form.Control
                                required
                                type="text"
                                name="price"
                                onChange={handleChange}
                            />
                            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                        </Form.Group>
                    </Row>
                    <br />
                    <Button type="submit">Create</Button>
                </Form>


                    // <form ref={formRef} onSubmit={addtocart}>
                    //     <input type="text" name="prod_Id" value={pro.prod_Id} onChange={handleChange}/><br />
                    //     <input type="text" name="delivery_charges" value='50' onChange={handleChange}/><br />
                    //     <input type="text" name="discount" value='500' onChange={handleChange}/><br />
                    //     <input type="text" name="price" value={pro.mrpPrice} onChange={handleChange}/><br />
                    //     <input type="text" name="quantity" value='2' onChange={handleChange}/><br />
                    //     <input type="text" name="total" value={pro.mrpPrice} onChange={handleChange}/><br />
                    //     <input type="text" name="cust_Id" value='null' onChange={handleChange}/><br />
                    //     <input type="submit"/><br />
                    // </form>
                ))}

            </div>
        </div>
    );
}
