import { useState, useEffect } from "react";
import Table from 'react-bootstrap/Table';
import Card from 'react-bootstrap/Card';

export function Categories(props) {
    const [Category, setCategories] = useState([]);
    const tdstyle = { border: "1px solid", padding: "10px" }
    useEffect(() => {
        fetch("http://localhost:8080/api/category")
            .then(res => res.json())
            .then((result) => { setCategories(result); }
            );
    }, []);

    return (
        <div>
            <Card style={{ width: '20rem', backgroundColor: "lightblue" }}>
                {/* <Card.Img variant="top" src={'/Images/' + product.proname + '.jpg'} /> */}
                <Card.Body>
                    {/* <Card.Title><b>product Details:</b></Card.Title> */}
                    <Card.Text>
                        <table style={{ border: "1px solid", borderCollapse: "collapse" }}>
                            <tr>
                                <td style={tdstyle}><label><b>Id:</b></label></td>
                                <td style={tdstyle}>{Category.catmasterid}</td>
                            </tr>
                            <tr>
                                <td style={tdstyle}><label><b>Name:</b></label></td>
                                <td style={tdstyle}>{a.proname}</td>
                            </tr>
                            <tr>
                                <td style={tdstyle}><label><b>Category:</b></label></td>
                                <td style={tdstyle}>{product.category}<br /></td>
                            </tr>
                            <tr>
                                <td style={tdstyle}><label><b>Quantity:</b></label></td>
                                <td style={tdstyle}>{product.quantity}<br /></td>
                            </tr>
                            <tr>
                                <td style={tdstyle}><label><b>Price:</b></label></td>
                                <td style={tdstyle}>{product.price}<br /></td>
                            </tr>

                        </table>
                    </Card.Text>
                </Card.Body>
            </Card>
        </div>
    );
}
