import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import React, { useRef } from 'react';
import './Product.css'
import Button from "react-bootstrap/esm/Button";
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

export function Product(props) {
    const [Product, setProduct] = useState([]);
    const { catmasterid } = useParams();
    useEffect(() => {
        fetch("http://localhost:8080/api/productsByCat/" + catmasterid)
            .then(res => res.json())
            .then((result) => { setProduct(result); }
            );
    }, []);

    const [Cart, setCart] = useState({});
    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setCart(values => ({ ...values, [name]: value }))
    }
    const addtocart = (event) => {
        let demo = JSON.stringify(Cart);
        fetch("http://localhost:8080/api/addCart", {
            method: 'POST',
            headers: { 'Content-type': 'application/json' },
            body: demo
        })
        event.preventDefault();
    }

    const formRef = useRef(null);
    const handleSubmitButtonClick = () => {
        // Call the submit method on the form element
        if (formRef.current) {
          formRef.current.submit();
        }
      };

    return (
        <div>
            <div className='catcontainer'>

                {Product.map(pro => (

                    <form ref={formRef} onSubmit={addtocart}>
                        <input type="text" name="prod_Id" value={pro.prod_Id} onChange={handleChange}/><br />
                        <input type="text" name="delivery_charges" value='50' onChange={handleChange}/><br />
                        <input type="text" name="discount" value='500' onChange={handleChange}/><br />
                        <input type="text" name="price" value={pro.mrpPrice} onChange={handleChange}/><br />
                        <input type="text" name="quantity" value='2' onChange={handleChange}/><br />
                        <input type="text" name="total" value={pro.mrpPrice} onChange={handleChange}/><br />
                        <input type="text" name="cust_Id" value='null' onChange={handleChange}/><br />
                        <input type="submit"/><br />
                    </form>
                ))}

            </div>
        </div>
    );
}
