import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import React, { useRef } from 'react';
import './Product.css'
import Button from "react-bootstrap/esm/Button";
import Card from "react-bootstrap/Card";

export function Product(props) {
    const [Product, setProduct] = useState([]);
    const { catmasterid } = useParams();
    useEffect(() => {
        fetch("http://localhost:8080/api/productsByCat/" + catmasterid)
            .then(res => res.json())
            .then((result) => { setProduct(result); }
            );
    }, []);

    const [Cart, setCart] = useState({});

    const addtocart = (event) => {
        setCart({ "prod_Id": Product.prod_Id, "deliveryCharges": 50, "discount": 50, "price": Product.mrpPrice, "quantity": 1, "total": Product.mrpPrice })
        let demo = JSON.stringify(Cart);
        fetch("http://localhost:8080/api/addCart", {
            method: 'POST',
            headers: { 'Content-type': 'application/json' },
            body: demo
        })
        event.preventDefault();
    }


    return (
        <div>
            <div className='catcontainer'>

                {Product.map(pro => (

                    <Card className="procard">
                        <Card.Body>
                        <Card.Img className="img" src={pro.prodImage}></Card.Img>
                        <Card.Title className="title">{pro.prodName}</Card.Title>
                        <Card.Text>{pro.prodDescription}</Card.Text>
                        <Card.Text><b>MRP:{pro.mrpPrice}</b></Card.Text>
                        <label><b>CardHolders Price:{pro.cardholdersPrice}</b></label><br />
                        <label><b>Points To Be Redeem:{pro.pointsToBeRedeem}</b></label><br />
                        <Button onClick={addtocart}>Add to cart</Button>
                        </Card.Body>
                    </Card>

                ))}

            </div>
        </div>
    );
}
