import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Table from 'react-bootstrap/Table';

export function Cart(props) {
    const [Cart, setCart] = useState([]);
    const Uobj = useSelector((state) => {
        return state.user;
    });
    useEffect(() => {
        fetch("http://localhost:8080/api/getCartByCustomer/" + Uobj.cust_Id)
            .then(res => res.json())
            .then((result) => { setCart(result); }
            );
    }, []);

    return (
        <div>
            <div className='catcontainer'>


                    // <>
                    //     <label><img src={cart.product.prodImage}></img></label>
                    //     <label>{cart.product.prodName}</label>
                    //     <label>{cart.product.prod_Id}</label>
                    //     <label>{cart.product.mrpPrice}</label>
                    // </>

                    <Table striped bordered hover>
                        {/* <thead>
                            <tr>
                                <th>Id</th>
                                <th>Name</th>
                                <th>Category</th>
                                <th>Quantity</th>
                                <th>Price</th>
                            </tr>
                        </thead> */}
                        <tbody>
                            {Cart.map(cart => (
                                <tr key={cart.cart_Id}>
                                    <td>{pro.proid}</td>
                                    <td>{pro.proname}</td>
                                    <td>{pro.category}</td>
                                    <td>{pro.quantity}</td>
                                    <td>{pro.price}</td>
                                    <td style={{ textAlign: "center" }}><Button variant="primary"><a style={mystyle1} href={'/Product/' + pro.proid}>View</a></Button></td>
                                    <td style={{ textAlign: "center" }}><Button variant="secondary"><a style={mystyle1} href={'/Proedit/' + pro.proid}>Edit</a></Button></td>
                                    <td style={{ textAlign: "center" }}><Button variant="danger"><a style={mystyle1} href={'/Prodel/' + pro.proid}>Delete</a></Button></td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>

            </div>
        </div>
    );
}
