import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import React, { useRef } from 'react';
import './Product.css'
import Button from "react-bootstrap/esm/Button";
import Card from "react-bootstrap/Card";

export function Product(props) {
    const [Product, setProduct] = useState([]);
    const { catmasterid } = useParams();
    useEffect(() => {
        fetch("http://localhost:8080/api/productsByCat/" + catmasterid)
            .then(res => res.json())
            .then((result) => { setProduct(result); }
            );
    }, []);

    // const [Cart, setCart] = useState({});

    // const addtocart = (event) => {
    //     {Product.map(pro => (
    //         setCart({ "prod_Id": pro.prod_Id, "deliveryCharges": 50, "discount": 50, "price": pro.mrpPrice, "quantity": 1, "total": pro.mrpPrice })
    //         ))}
        
    //     console.log(Cart);
    //     // const cart = {
    //     //     "prod_Id": Product.prod_Id, 
    //     //     "deliveryCharges": 50, 
    //     //     "discount": 50, 
    //     //     "price": Product.mrpPrice, 
    //     //     "quantity": 1, 
    //     //     "total": Product.mrpPrice
    //     // };
    //     let demo = JSON.stringify(Cart);
    //     fetch("http://localhost:8080/api/addCart", {
    //         method: 'POST',
    //         headers: { 'Content-type': 'application/json' },
    //         body: demo
    //     })
    //     //event.preventDefault();
    //     alert("Product added to cart");
    // }


    return (
        <div>
            <div className='catcontainer'>

                {Product.map(pro => (

                    <Card className="procard">
                        <Card.Body>
                        <Card.Img className="img" src={pro.prodImage}></Card.Img><br/><br/>
                        <Card.Title className="title">{pro.prodName}</Card.Title>
                        <Card.Text>{pro.prodDescription}</Card.Text>
                        <Card.Text>
                            <b>MRP:{pro.mrpPrice}</b><br/>
                            CardHolders Price:{pro.cardholdersPrice}<br/>
                            Points To Be Redeem:{pro.pointsToBeRedeem}
                        </Card.Text>
                        <Button >Add to cart</Button>
                        </Card.Body>
                    </Card>

                ))}

            </div>
        </div>
    );
}
