import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export function AddCart() {

    const [Cart, setCart] = useState({});
    const [Product, setProduct] = useState({});
    const { proid } = useParams();

    useEffect(() => {
        fetch("http://localhost:8080/api/getProductById/" + proid)
            .then(res => res.json())
            .then((result) => { setProduct(result); }
            );
    }, []);

    setCart({ "prod_Id": pro.prod_Id, "deliveryCharges": 50, "discount": 50, "price": pro.mrpPrice, "quantity": 1, "total": pro.mrpPrice })
    console.log(Cart);

        let demo = JSON.stringify(Cart);
        fetch("http://localhost:8080/api/addCart", {
            method: 'POST',
            headers: { 'Content-type': 'application/json' },
            body: demo
        })
        alert("Poduct added to cart")

    return(
        <h1>Product added to cart</h1>
    )

}
