import { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import { useNavigate } from "react-router-dom";
import "./Cart.css";

export function Cart(props) {
    const [total, setTotal] = useState(0);
    const [totalredeem, setTotalredeem] = useState(0);
    let navigate = useNavigate();
    const [Cart, setCart] = useState([]);
    const Uobj = useSelector((state) => {
        return state.user;
    });
    useEffect((event) => {
        fetch("http://localhost:8080/api/getCartByCustomer/" + Uobj.cust_Id)
            .then(res => res.json())
            .then((result) => { setCart(result); 
                let t = 0, r = 0;
                result.forEach(e => {
                    t += e.product.mrpPrice;
                    r += e.product.pointsToBeRedeem
                    setTotal(t);
                    setTotalredeem(r);
                });
            }
            );
            
    }, []);

    const handleDelete = (event, cart) => {
        fetch("http://localhost:8080/api/deleteCart/" + cart.cart_Id, {
            method: 'Delete'
        })
        navigate('/Home');
    }

    const dototal = (e) =>{
        if(e.target.checked)
            setTotal(total-totalredeem);
        else
            setTotal(total+totalredeem);
    }

    const [quantity, setQuantity] = useState(1);
    const setquant = (e) => {
        
    }

    return (
        <div>
            <div className='cartcontainer'>
                    <h2>*Cart*</h2>
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th></th>
                                <th>Product Name</th>
                                <th>Price</th>
                                <th>Redeem Points</th>
                                <th>Quantity</th>
                            </tr>
                        </thead>
                        <tbody>
                            {Cart.map(cart => (
                                <tr key={cart.cart_Id}>
                                    <td><img src={cart.product.prodImage}></img></td>
                                    <td>{cart.product.prodName}</td>
                                    <td>₹{cart.product.mrpPrice}</td>
                                    <td>{cart.product.pointsToBeRedeem}</td>
                                    <td><button>+</button>{quantity}<button>-</button></td>
                                    <td><Button variant="danger" onClick={(event) => handleDelete(event, cart)}>Remove</Button></td>
                                </tr>
                                
                            ))}
                            <tr>
                                <td>Total</td>
                                <td></td>
                                <td>₹{total}</td>
                                <td><input name="rp" type="checkbox" onChange={dototal}/>{totalredeem}</td>
                                <td></td>
                            </tr>
                        </tbody>
                    </Table>

            </div>
        </div>
    );
}
