import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, Button } from "react-bootstrap";

export default function Cart1() {
  const [cartItems, setCartItems] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/getByCustId/1")
      .then((res) => res.json())
      .then((cartItems) => {
        setCartItems(cartItems);

        // Fetch products using the retrieved cart items
        const productPromises = cartItems.map((item) =>
          fetch(
            "http://localhost:8080/api/getProductsById/" + item.prod_Id
          ).then((res) => res.json())
        );

        Promise.all(productPromises)
          .then((productResults) => {
            setProducts(productResults);
          })
          .catch((error) => {
            console.error("Error fetching products:", error);
          });
      })
      .catch((error) => {
        console.error("Error fetching cart items:", error);
      });
  }, []);

  const handleRemove = (itemId) => {
    fetch("http://localhost:8080/api/deleteByProdId/" + itemId, {
      method: "DELETE",
    })
      .then((res) => {
        if (res.ok) {
          setCartItems((prevItems) =>
            prevItems.filter((item) => item.id !== itemId)
          );
        } else {
          console.error("Failed to delete item from the database.");
        }
      })
      .catch((error) => {
        console.error("Error deleting item:", error);
      });
  };

  return (
    <Container>
      <h1>Your Cart</h1>
      {products.map((item) => (
        <Card key={item.id} className='mb-3'>
          <Card.Body>
            <Row>
              <Col md={4}>
                <img
                  src={item.prodImage}
                  alt={item.prodName}
                  className='img-fluid'
                />
              </Col>
              <Col md={8}>
                <h5>{item.prodName}</h5>
                <p>Price: ${item.cardholdersPrice}</p>
                <p>Quantity: 1</p>
                <Button
                  variant='danger'
                  size='sm'
                  onClick={() => handleRemove(item.prod_Id)}
                >
                  Remove
                </Button>
              </Col>
            </Row>
          </Card.Body>
        </Card>
      ))}
      <div className='text-right'>
        <h4>Total: </h4>
        <Button variant='primary'>Checkout</Button>
      </div>
    </Container>
  );
}
