import { useState, useEffect } from "react";
import { useParams } from "react-router-dom"

export function Categories() {
    const [Category, setCategories] = useState({});
    const { catmasterid } = useParams()
    useEffect(() => {
        fetch("http://localhost:8080/api/categories")
            .then(res => res.json())
            .then((result) => { setCategories(result); }
            );
    }, {});

    return (
        <div>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                        <th>Category</th>
                        <th>Quantity</th>
                        <th>Price</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map(pro => (
                        <tr key={pro.proid}>
                            <td>{pro.proid}</td>
                            <td>{pro.proname}</td>
                            <td>{pro.category}</td>
                            <td>{pro.quantity}</td>
                            <td>{pro.price}</td>
                            <td style={{textAlign:"center"}}><Button variant="primary"><a style={mystyle1} href={'/Product/' + pro.proid}>View</a></Button></td>
                            <td style={{textAlign:"center"}}><Button variant="secondary"><a style={mystyle1} href={'/Proedit/' + pro.proid}>Edit</a></Button></td>
                            <td style={{textAlign:"center"}}><Button variant="danger"><a style={mystyle1} href={'/Prodel/' + pro.proid}>Delete</a></Button></td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
    );
}
