import { useState, useEffect } from "react";
import './Categories.css'
import { Link, useParams } from "react-router-dom";

export function Categories(props) {
    const [Category, setCategories] = useState([]);
    const { subcatid } = useParams();
    useEffect(() => {
        fetch("http://localhost:8080/api/category/"+'null')
            .then(res => res.json())
            .then((result) => { setCategories(result); }
            );
    }, []);

    useEffect(() => {
        fetch("http://localhost:8080/api/category/"+ subcatid)
            .then(res => res.json())
            .then((result) => { setCategories(result); }
            );
    }, []);

    return (
        <div>
            <div className='catcontainer'>

                {Category.map(cat => (
                    
                    <Link to={'/CategoryBySubcatId/'+cat.cat_id}>
                        <div className='citem'>
                            <img className='cimg' src={cat.catImgPath}></img><br />
                            <label><b>{cat.categoryName}</b></label><br />
                        </div>
                    </Link>

                ))}

            </div>
        </div>
    );
}
