import { useState, useEffect } from "react";
import Card from 'react-bootstrap/Card';
import './Categories.css'

export function Categories(props) {
    const [Category, setCategories] = useState([]);
    useEffect(() => {
        fetch("http://localhost:8080/api/category")
            .then(res => res.json())
            .then((result) => { setCategories(result); }
            );
    }, []);

    return (
        <div>
            <div className='catcontainer'>

                {Category.map(cat => (

                    <a href={"/CategoryBySubcatId/"+subcat_id}>
                        <div className='citem'>
                            <img className='cimg' src={cat.catImgPath}></img><br />
                            {/* <label>{pro.proid}</label> */}
                            <label><b>{cat.categoryName}</b></label><br />
                            {/* <label>{pro.category}</label> */}
                            {/* <label>{pro.quantity}</label> */}
                            {/* <label><b>â‚¹{pro.price}</b></label> */}

                        </div>
                    </a>

                ))}

            </div>
        </div>
    );
}
